import React, { Component } from "react";
import NavbarComponen from "./NavbarComponen";
import Tabel from "./Tabel";
import Formulir from "./Formulir";

export default class Crud extends Component {
  constructor(props) {
    super(props);

    this.state = {
      makanans: [],
      nama: "",
      deskripsi: "",
      harga: 0,
      id: "",
    };
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();

    if (this.state.id === "") {
      this.setState({
        makanans: [
          ...this.state.makanans,
          {
            id: this.state.makanans.length + 1,
            nama: this.state.nama,
            deskripsi: this.state.deskripsi,
            harga: this.state.harga,
          },
        ],
      });
    }

    this.setState({
      makanans: [
        ...this.state.makanans,
        {
          id: this.state.makanans.length + 1,
          nama: this.state.nama,
          deskripsi: this.state.deskripsi,
          harga: this.state.harga,
        },
      ],
    });
    this.setState({
      nama: "",
      deskripsi: "",
      harga: 0,
      id: "",
    });
  };

  editData = (id) => {
    const MakananYangDipilih = this.state.makanans
      .filter((makanan) => makanan.id === id)
      .map((FilterMakanan) => {
        return FilterMakanan;
      });

    this.setState({
      nama: MakananYangDipilih[0].nama,
      deskripsi: MakananYangDipilih[0].deskripsi,
      harga: MakananYangDipilih[0].harga,
      id: MakananYangDipilih[0].id,
    });
  };

  render() {
    //console.log(this.state.makanans);
    return (
      <div>
        <NavbarComponen />
        <div className="container mt-4">
          <Tabel makanans={this.state.makanans} editData={this.editData} />
          <Formulir
            {...this.state}
            handleChange={this.handleChange}
            handleSubmit={this.handleSubmit}
          />
        </div>
      </div>
    );
  }
}
