import React, { Component } from "react";
import { Table } from "react-bootstrap";
import { Row, Col, Form, Button } from "react-bootstrap";
class Tabel extends Component {
  constructor(props) {
    super(props);
    // this.handleSubmit = this.handleSubmit.bind(this);

    this.state = {
      DataSkill: [],
      isLoaded: false,

      skillID: "",
      skillName: "",
    };
  }

  componentDidMount() {
    const apiUrl = "https://localhost:44383/api/Skill/GetSkills";

    fetch(apiUrl)
      .then((res) => res.json())
      .then(
        (result) => {
          console.log("result");
          this.setState({
            DataSkill: result,
          });
        },
        (error) => {
          this.setState({ error });
        }
      );
  }

  deleteSkill = (skill_id) => {
    const { app_skills } = this.state;
    const apiUrl =
      "https://localhost:44383/api/Skill/Delete?idSkill=" + skill_id;
    const options = {
      method: "DELETE",
    };

    fetch(apiUrl, options);

    window.location.reload();
    this.setState({
      app_skills: newapp_skill,
    });
  };

  render() {
    var { isLoaded, DataSkill } = this.state;

    {
      return (
        <div className="App">
          <table className="table">
            <thead>
              <tr>
                <th>Skill ID</th>
                <th>Skill Name</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {DataSkill.map((data) => (
                <tr key={data.skillId}>
                  <td>{data.skillId}</td>
                  <td>{data.skillName}</td>
                  <td>
                    <td>
                      {/* <Button variant="info" onClick={() => this.props.editProduct(product.id)}>Edit</Button> */}
                      &nbsp;
                      <Button
                        variant="danger"
                        onClick={() => this.deleteSkill(data.skillId)}
                      >
                        Delete
                      </Button>
                    </td>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  }
}

