import React, { Component } from "react";
import { Table } from "react-bootstrap";

class Tabel extends Component {
  constructor(props) {
    super(props);
    // this.handleSubmit = this.handleSubmit.bind(this);

    this.state = {
      DataSkill: [],
      isLoaded: false,

      skillID: "",
      skillName: "",
    };
  }

  componentDidMount() {
    const apiUrl = "https://localhost:44383/api/Skill/GetSkills";

    fetch(apiUrl)
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          this.setState({
            DataSkill: result,
          });
        },
        (error) => {
          this.setState({ error });
        }
      );
  }

  render() {
    var { isLoaded, DataSkill } = this.state;

    if (!isLoaded) {
      return <div>Loading..</div>;
    } else {
      return (
        <div className="App">
          <ul>
            {DataSkill.map((item) => (
              <li key={item.id}>
                Id:{item.skillID} | Name:{item.skillName}
              </li>
            ))}
            ;
          </ul>
        </div>
      );
    }
  }
}

