import React, { Component } from 'react'

export default class StateProps extends Component {
    constructor(props){
        super(props);
        this.state = {
            makanan : 'Mieayam'
        }
    }
    render() {
        return (
            <div>
                <h2>{this.state.makanan}</h2> 
                <button onClick={() => this.setState({ makanan:"Soto"})}>Ganti makanan</button>
            </div>
        )
    }
}
